import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import WordColumn from './WordColumn';

const words = [
  { english: 'forest', french: 'forêt' },
  { english: 'sibling', french: 'frère et sœur' },
  { english: 'cereal', french: 'céréale' },
  { english: 'desk', french: 'bureau' },
  { english: 'camel', french: 'chameau' },
  { english: 'butter', french: 'beurre' },
  { english: 'bicycle', french: 'vélo' },
  { english: 'railroad', french: 'chemin de fer' },
  { english: 'folder', french: 'dossier' },
  { english: 'weekly', french: 'hebdomadaire' },
  { english: 'hungry', french: 'faim' },
  { english: 'limestone', french: 'calcaire' },
];

function App() {
  const [englishWords, setEnglishWords] = useState(words.map((w) => ({ word: w.english, isDragging: false })));
  const [frenchWords, setFrenchWords] = useState(words.map((w) => ({ word: w.french, isDragging: false })).sort(() => Math.random() - 0.5));
  const [buttonText, setButtonText] = useState('GO');
  const [grade, setGrade] = useState(null);

  const handleButtonClick = () => {
    if (buttonText === 'GO') {
      setButtonText('GRADE');
      setGrade(null);
    } else {
      const correct = englishWords.filter((ew, i) => ew.word === words.find((w) => w.french === frenchWords[i].word).english).length;
      const percentage = (correct / englishWords.length) * 100;
      setGrade(percentage);
      setButtonText('GO');
    }
  };

  const handleDrop = (index) => {
    console.log("handleDrop Index: ", index);
    const droppedWord = frenchWords.find((w) => w.isDragging);
    if (droppedWord) {
      console.log("handleDrop: ", droppedWord);
      const newEnglishWords = [...englishWords];
      newEnglishWords[index] = { ...newEnglishWords[index], word: droppedWord.word };
      setEnglishWords(newEnglishWords);

      const newFrenchWords = frenchWords.map((w) => {
        if (w.word === droppedWord.word) {
          return { ...w, isDragging: false };
        }
        return w;
      });
      setFrenchWords(newFrenchWords.filter((w) => w.word !== droppedWord.word));
    }
  };

  const handleDragEnd = (item, dropResult) => {
    console.log("handleDragEnd(item, dropResult): ",item, dropResult);
    if (!dropResult) {
      console.log("Not drop result?");
      const newFrenchWords = frenchWords.map((w) => {
        if (w.word === item.word) {
          return { ...w, isDragging: false };
        }
        return w;
      });
      setFrenchWords(newFrenchWords);
    }
  };



  return (
    <div className="App">
      <button onClick={handleButtonClick}>{buttonText}</button>
      {grade !== null && <p>Percentage Correct: {grade.toFixed(2)}%</p>}

      <DndProvider backend={HTML5Backend}>
        <div style={{ display: 'flex', justifyContent: 'space-around' }}>
          <WordColumn 
            words={englishWords} 
            onDrop={() => {}} 
          />
          <WordColumn 
            words={englishWords} 
            onDrop={handleDrop} 
            emptySlots 
          />
          <WordColumn
            words={frenchWords}
            onDrop={() => {}}
            onDragEnd={handleDragEnd}
          />
        </div>
      </DndProvider>
    </div>
  );
}

export default App;
