import React from 'react';

const WordCard = ({ word, isFrench, onClick, isSelected }) => {
  const style = {
    border: '1px solid black',
    padding: '5px',
    margin: '5px',
    backgroundColor: isSelected ? 'cyan' : 'white',
  };

  return (
    <div onClick={() => onClick(isFrench ? word : word.index)} style={style}>
      {word.text}
    </div>
  );
};

export default WordCard;
