import React from 'react';
import { useDrop } from 'react-dnd';
import WordCard from './WordCard';

const WordColumn = ({ words, onDrop, emptySlots }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'WORD',
    drop: onDrop,
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  const style = {
    border: '1px solid black',
    minHeight: '100px',
    minWidth: '200px',
    padding: '10px',
    display: 'grid',
    gridTemplateRows: `repeat(\${words.length}, 1fr)`,
  };

  return (
    <div ref={drop} style={style}>
      {emptySlots &&
        words.map((_, index) => (
          <div key={index} style={{ border: '1px dashed gray', minHeight: '20px', margin: '5px' }} />
        ))}
      {words.map((word, index) => (
        <WordCard key={index} word={word} isFrench={!emptySlots} />
      ))}
    </div>
  );
};

export default WordColumn;
