import React from 'react';
import WordCard from './WordCard';

const WordColumn = ({ words, onClick, selectedWord, language, connections, pairedWords }) => {
    //Grab connections for each word, and return the desired language
    const getConnectionForLanguage = () => {
        if (language === 'english') {
          return connections.filter((c) => c.englishWord.language === 'english');
        } else {
          return connections.filter((c) => c.frenchWord.language === 'french');
        }
    };
    const connectionsForLanguage = getConnectionForLanguage();

    //Custom Styling for borders
    const style = {
    border: '1px solid black',
    minHeight: '100px',
    minWidth: '200px',
    padding: '10px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'relative',
  };

  return (
    <div style={style}>
      {words.map((word, index) => {

        //Word pairing state variable, creates yellow highlight when paired
        const isPaired = pairedWords.some(
            (pair) => pair[`\${language}Word`].word.index === index && pair.color === 'yellow'
        );

        return (
            <WordCard
                key={index}
                word={{ text: word, index }}
                onClick={onClick}
                isSelected={(selectedWord && selectedWord.word.index === index && selectedWord.language === language) || isPaired }
                language={language}
            />
        );
      })}
      <svg style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', pointerEvents: 'none' }}>
        {connectionsForLanguage.map((c, i) => (
          <line
            key={i}
            x1={c.englishWord.offsetLeft + c.englishWord.offsetWidth}
            y1={c.englishWord.offsetTop + c.englishWord.offsetHeight / 2}
            x2={c.frenchWord.offsetLeft}
            y2={c.frenchWord.offsetTop + c.frenchWord.offsetHeight / 2}
            stroke="black"
            strokeWidth="2"
          />
        ))}
      </svg>
    </div>
  );
};

export default WordColumn;
