import React, { useState } from 'react';
import WordColumn from './WordColumn';

const words = [
  { english: 'forest', french: 'forêt' },
  { english: 'sibling', french: 'frère et sœur' },
  { english: 'cereal', french: 'céréale' },
  { english: 'desk', french: 'bureau' },
  { english: 'camel', french: 'chameau' },
  { english: 'butter', french: 'beurre' },
  { english: 'bicycle', french: 'vélo' },
  { english: 'railroad', french: 'chemin de fer' },
  { english: 'folder', french: 'dossier' },
  { english: 'weekly', french: 'hebdomadaire' },
  { english: 'hungry', french: 'faim' },
  { english: 'limestone', french: 'calcaire' },
];

function App() {
  const [selectedWord, setSelectedWord] = useState(null);
  const [connections, setConnections] = useState([]);
  const [buttonText, setButtonText] = useState('GO');
  const [grade, setGrade] = useState(null);

  const handleWordClick = (word, language) => {
    if (selectedWord && selectedWord.language !== language) {
      if (selectedWord.language === 'english') {
        setConnections([...connections, { englishWord: selectedWord.word, frenchWord: word }]);
      } else {
        setConnections([...connections, { englishWord: word, frenchWord: selectedWord.word }]);
      }
      setSelectedWord(null);
    } else {
      setSelectedWord({ word, language });
    }
  };

  const handleButtonClick = () => {
    if (buttonText === 'GO') {
      setButtonText('GRADE');
      setGrade(null);
    } else {
      const correct = connections.filter(
        (c) => words.find((w) => w.english === c.englishWord).french === c.frenchWord
      ).length;
      const percentage = (correct / words.length) * 100;
      setGrade(percentage);
      setButtonText('GO');
    }
  };

  return (
    <div className="App">
      <button onClick={handleButtonClick}>{buttonText}</button>
      {grade !== null && <p>Percentage Correct: {grade.toFixed(2)}%</p>}
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <WordColumn
          words={words.map((w) => w.english)}
          onClick={handleWordClick}
          selectedWord={selectedWord}
          language="english"
        />
        <WordColumn
          words={words.map((w) => w.french).sort(() => Math.random() - 0.5)}
          onClick={handleWordClick}
          selectedWord={selectedWord}
          language="french"
        />
      </div>
      <svg style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
        {connections.map((c, i) => (
          <line
            key={i}
            x1={c.englishWord.offsetLeft + c.englishWord.offsetWidth}
            y1={c.englishWord.offsetTop + c.englishWord.offsetHeight / 2}
            x2={c.frenchWord.offsetLeft}
            y2={c.frenchWord.offsetTop + c.frenchWord.offsetHeight / 2}
            stroke="black"
            strokeWidth="2"
          />
        ))}
      </svg>
    </div>
  );
}

export default App;
