import React from 'react';
import { useDrop } from 'react-dnd';
import WordCard from './WordCard';

const WordColumn = ({ words, onDrop, emptySlots, onDragEnd }) => {
  const [{ isOver }, drop] = useDrop({
    accept: 'word',
    drop: (item) => {
      onDrop(item);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <div ref={drop} style={{ minHeight: 60, padding: 10, backgroundColor: isOver ? 'lightblue' : 'white' }}>
      {words.map((word, i) => (
        <WordCard key={i} word={word} onDragEnd={onDragEnd} />
      ))}
      {emptySlots && (
        <div style={{ minHeight: 60 * (words.length + 1), border: '1px dashed black', marginTop: 10 }} />
      )}
    </div>
  );
};

export default WordColumn;
