import React from 'react';
import WordCard from './WordCard';

const WordColumn = ({ words, onClick, selectedWord, language }) => {
  const style = {
    border: '1px solid black',
    minHeight: '100px',
    minWidth: '200px',
    padding: '10px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
  };

  return (
    <div style={style}>
      {words.map((word, index) => (
        <WordCard
          key={index}
          word={{ text: word, index }}
          onClick={onClick}
          isSelected={selectedWord && selectedWord.word.index === index && selectedWord.language === language}
          language={language}
        />
      ))}
    </div>
  );
};

export default WordColumn;
