import React, {useRef} from 'react';

const WordCard = ({ word, onClick, isSelected, language, pairedWords = [] }) => {
  const pairedColor = pairedWords.some(pair => (
    pair[language + "Word"] && pair[language + "Word"].word.index === word.index && pair[language + "Word"].language === language
  )) ? "yellow" : null;

  const style = {
    border: '1px solid black',
    borderRadius: '5px',
    padding: '5px',
    margin: '5px',
    backgroundColor: isSelected || pairedColor,
  };

  const wordRef = useRef(null);

  const handleClick = () => {
    onClick(language, word, wordRef);
  };

  return (
    <div ref={wordRef} style={style} onClick={handleClick}>
      {word.text}
    </div>
  );
};

export default WordCard;
