import React, { useRef, useState, useEffect } from 'react';

const WordCard = ({ word, language, onClick, isSelected, pairedWords=[] }) => {
  const wordRef = useRef(null);
  const [color, setColor] = useState('white');

  useEffect(() => {
    setColor(pairedWords.some(pair => pair[language + "Word"].word.index === word.index && pair.color === 'yellow') ? 'yellow' : 'white');
  }, [pairedWords]);

const style = {
    border: '1px solid black',
    padding: '5px',
    margin: '5px',
    backgroundColor: isSelected || (
        pairedWords.some(pair => (
        pair[language + "Word"] && pair[language + "Word"].word.index === word.index && pair[language + "Word"].language === language
        ))
        ? (isSelected === "cyan" && word.index === word.index && language === language ? "cyan" : "yellow")
        : null
    ),
};

  return (
    <div
      onClick={() => onClick(word, language, wordRef)}
      style={style}
      ref={wordRef}
    >
      {word.text}
    </div>
  );
};

export default WordCard;
